import { useState , useEffect } from "react";
import { Link } from "react-scroll";

function NavBar(){
    const [navActive, setNavActive] = useState(false);;

    const toggleNav = () => {
        setNavActive(!navActive);
    };

    const closeMenu = () => {
        setNavActive(false);
    };

    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth <= 500){
                closeMenu();
            }
        };
        window.addEventListener("resize",handleResize);
        return  () => {
            window.removeEventListener("resize",handleResize);
        };
    }, []);

    useEffect(() => {
        if (window.innerWidth <= 1200){
            closeMenu();
        }
    },[]);

    return(
        <nav className={`navbar ${navActive ? "active" : ""}`}>
            <div>
                <img src="./img/logo.svg" alt="logoipsum" />
            </div>
            <a className={`nav__hamburguer ${navActive ? "active" : ""}`} onClick={toggleNav}>
                <span className="nav__hamburguer__line"></span>
                <span className="nav__hamburguer__line"></span>
                <span className="nav__hamburguer__line"></span>
            </a>
            <div className={`navbar--items ${navActive ? "active" : ""}`}>
            <ul>
                <li>
                    <Link 
                    onClick={closeMenu} 
                    activeClass="navbar--active--content" 
                    spy={true} 
                    smooth={true} 
                    offset={-70} 
                    duration={500} 
                    to="HeroSection" 
                    className="navbar--content">
                        Inicio
                    </Link>
                </li>
                <li>
                    <Link 
                    onClick={closeMenu} 
                    activeClass="navbar--active--content" 
                    spy={true} 
                    smooth={true} 
                    offset={-70} 
                    duration={500} 
                    to="MySkills" 
                    className="navbar--content">
                        Mis habilidades
                    </Link>
                </li>
                <li>
                    <Link 
                    onClick={closeMenu} 
                    activeClass="navbar--active--content" 
                    spy={true} 
                    smooth={true} 
                    offset={-70} 
                    duration={500} 
                    to="MyPortfolio" 
                    className="navbar--content">
                        Experiencia
                    </Link>
                </li>
                <li>
                    <Link 
                    onClick={closeMenu} 
                    activeClass="navbar--active--content" 
                    spy={true} 
                    smooth={true} 
                    offset={-70} 
                    duration={500} 
                    to="AboutMe" 
                    className="navbar--content">
                        Acerca de mi
                    </Link>
                </li>
            </ul>
        </div>
        <Link 
            onClick={closeMenu} 
            activeClass="navbar--active--content" 
            spy={true} 
            smooth={true} 
            offset={-70} 
            duration={500} 
            to="Contact" 
            className="btn btn-outline-primary">
                Contactame
        </Link>
        </nav>
    )
}

export default NavBar;
